import { UserActionTypes } from "./user.types";

const INITAIL_STATE = {
    currentUser: null,
    error: null
}

const userReducer = ( state = INITAIL_STATE , action ) => {
    switch (action.type) {
        case UserActionTypes.SET_CURRENT_USER:
            return {
                ...state,
                currentUser: action.payload
            };

        default: 
            return state;
    }
}

export default userReducer;
